import { createSlice } from "@reduxjs/toolkit"
import cartItems from "../../cartItems"

const initialState = {
    cartItems: cartItems,
    amount: 4,
    total: 0,
    isLoading: true,
}

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        clearCart: (state) => {
            state.cartItems = []
            // return {cartItems: []} it is going to update the initialState, removing what's not passed inside of the return.
        }, 
        removeItem: (state,action) => {
            console.log(action)
        }
    }
})

// console.log(cartSlice)

export const { clearCart, removeItem } = cartSlice.actions

export default cartSlice.reducer
